import { createApp } from "vue";
import { createStore } from 'vuex'
import App from "./App.vue";

import AppModal from "./components/AppModal.vue";
const app = createApp(App);


// Create a new store instance.
const store = createStore({
    //state nó giống như data vậy
    state() {
        return {
            userList: [{
                    "id": 1,
                    "name": "Đoàn Hải Long",
                    "avatar": "https://haycafe.vn/wp-content/uploads/2021/12/Hinh-anh-avatar-nam-cool-ngau-dep.jpg",
                    "age": 23,
                    "description": "thân thiện , hoc hỏi nhanh",
                    "programmingLanguage": ["JS", "PHP", "C++"],
                    "gender": "Nam",
                    "type": "ADMIN"
                },
                {
                    "id": 1,
                    "name": "Trần Quỳnh Giang",
                    "avatar": "https://www.facebook.com/photo/?fbid=651123716303139&set=a.100997521315764",
                    "age": 22,
                    "description": "thân thiện , hoc hỏi nhanh",
                    "programmingLanguage": ["PHP", "C#"],
                    "gender": "Nữ",
                    "type": "CLIENT"
                }
            ]

        }

    }
})
app.use(store)
app.component("app-modal", AppModal);

app.mount("#app");
